import org.efaps.admin.runlevel.RunLevel;
import org.efaps.db.Context;
import org.efaps.db.SearchQuery;
import org.efaps.esjp.admin.ui.UIUpdate;
import org.efaps.importer.DataImport;

import java.net.URL
import java.io.File;

EFAPS_LOGGER.info("Importing Data for efaps Localization module;");

RunLevel.init("shell");
RunLevel.execute();

SearchQuery query1 = new SearchQuery();
query1.setQueryTypes("Ubicaciones_UbicacionStandardRoot");
query1.addWhereExprEqValue("Name", "Amazonas");
query1.addSelect("OID");
query1.execute();
if (!query1.next()) {
    URL url1 = new URL(EFAPS_ROOTURL, "Data/IMPORT_Amazonas.xml");
    DataImport imp1 = DataImport.readFile(url1);
    imp1.updateInDB();
}
