SearchQuery query1 = new SearchQuery();
query1.setQueryTypes("Ubicaciones_UbicacionStandardRoot");
query1.addWhereExprEqValue("Name", "Amazonas");
query1.addSelect("OID");
query1.execute();
if (!query1.next()) {
    URL url1 = new URL(EFAPS_ROOTURL, "Data/IMPORT_Amazonas.xml");
    DataImport imp1 = DataImport.readFile(url1);
    imp1.updateInDB();
}
