version.script:
import org.efaps.admin.runlevel.RunLevel;
import org.efaps.db.Context;
import org.efaps.db.SearchQuery;
import org.efaps.esjp.admin.ui.UIUpdate;
import org.efaps.importer.DataImport;

import java.net.URL
import java.io.File;

EFAPS_LOGGER.info("Importing Data for efaps Localization module;");

RunLevel.init("shell");
RunLevel.execute();

SearchQuery query = new SearchQuery();
query.setQueryTypes("Ubicaciones_UbicacionStandardRoot");
query.addWhereExprEqValue("Name", "Lima");
query.addSelect("OID");
query.execute();
if (!query.next()) {

    URL url1 = new URL(EFAPS_ROOTURL, "Data/IMPORT_Lima.xml");
    DataImport imp1 = DataImport.readFile(url1);
    imp1.updateInDB();
}

